import React from 'react';
import './App.css';
import axios from 'axios';

export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      available: [],
      info: '',
      champs: '',
      champsCurrent: '',
      champsSeason: '',
      error: null,
      names: ''
    };
    this.selectRegion = this.selectRegion.bind(this);
  }

    selectRegion = async (e) => {
      let idx = e.target.selectedIndex;
      const index = this.state.info[idx].id;
      await axios.get(`http://api.football-data.org/v2/competitions/${index}`,
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
            this.setState({champs: res.data,
            champsCurrent: res.data.currentSeason,
            champsSeason: res.data.seasons,
            error:null})
        }).catch(err => {
          console.log('error')
          this.setState({
            error:"true"
          })
        })
    };

    async componentWillMount() {
      await axios.get("http://api.football-data.org/v2/competitions/",
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
          var listNames = [];
          let infoList = res.data.competitions.map((item, i) => {
            axios.get(`http://api.football-data.org/v2/competitions/${item.area.id}`,
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
          ).then(res => {
            // console.log(res.data)
              this.setState({available: this.state.available.push(res.data),
              champs: res.data,
              champsCurrent: res.data.currentSeason,
              champsSeason: res.data.seasons,
              error:null})
              console.log(this.state.available)
          }).catch(err => {
          console.log('error')
          this.setState({
            error:"true"
          })
        })
            return(
              listNames.push({
                key: 'name',
                value: item.area.name
              })
            )
          }, this);
          this.setState({info: res.data.competitions,
          names: listNames}); 
        })
        // console.log(this.state.names[0].value)

        // await axios.get(`http://api.football-data.org/v2/competitions/${index}`,
        //   { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
        //   'content-type' : 'application/json'}}
        // ).then(res => {
    }

  render() {
    const { info, champs, champsCurrent, champsSeason, error } = this.state;
    let seasonList = ((error===null) &&  champsSeason) ? champsSeason.map((item, i) => (
          <div key={i}>
            <p key={i}>{'Id season: '}{ item.id }</p>
            <li>{'Start date: '}{ item.startDate }</li>
            <li>{'End date: '}{ item.endDate }</li> 
          </div>

      )) : (<div key={0}>
        <p>{"Dados indisponÃ­veis"}</p>
      </div>)


    let infoList = info.length > 0 && info.map((item, i) => {
      return(
        <option key={i} value={info[i].id}>{item.area.name}</option>
      )
    }, this);


    return(
      <div>
        <p>Select your region:</p>
        <select onChange={this.selectRegion}>
          { infoList }
        </select>
        <p>Current Season</p>
        <ul>
          { champs.name }{ ' - ' }{ champs.code }
          <li>{'Start date: '}{ champsCurrent.startDate }</li>
          <li>{'End date: '}{ champsCurrent.endDate }</li>
        </ul>
        <p>Previous Seasons</p>
        <ul>
          { seasonList }
        </ul> 
      </div>
    );
  }
}
