import React from 'react';
import './App.css';
import axios from 'axios';

export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      info: '',
      champs: '',
      champsCurrent: '',
      champsSeason: '',
      error: ''
    };
    this.selectRegion = this.selectRegion.bind(this);
  }
  // postLogin = async () => {
  //   const request = {
  //     method: 'POST',
  //     url: 'aaa',
  //     responseType: 'json'
  //   }
  // }
    //request.headers = {Authorization: token}
    // request.data = this.state
    // const response = await axios (request)
    // return response 
    // console.log('telho')

    selectRegion = async (e) => {
      let idx = e.target.selectedIndex;
      const index = this.state.info[idx].id;
      await axios.get("http://api.football-data.org/v2/competitions/"+index.toString(),
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
          // console.log(res);
          // if(res){
            this.setState({champs: res.data,
            champsCurrent: res.data.currentSeason,
            champsSeason: res.data.seasons})
          // }
          // else{
          //   alert("Dados indisponíveis");
          //   console.log("aqui")
          // }
          //PRECISO ARRUMAR O ESQUEMA DO ERRO
        }).catch(err => console.log("se fudeu"))
    };

    async componentWillMount() {
      await axios.get("http://api.football-data.org/v2/competitions/",
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
          // console.log(res.data.competitions);
          this.setState({info: res.data.competitions}); 
        })
    }

  render() {
    const { info, champs, champsCurrent, champsSeason, error } = this.state;
    let seasonList = champsSeason.length > 0 && champsSeason.map((item, i) => {
      if(i !== 0){
        return(
          <div key={i}>
            <p key={i}>{'Id season: '}{ item.id }</p>
            <li>{'Start date: '}{ item.startDate }</li>
            <li>{'End date: '}{ item.endDate }</li>
          </div>
        )
      }
      else{
        return(
          <div key={i}>
          <p>{'NÃO TEM NADA'}</p>
          </div>
        )
      }
      // if(error !== ''){
      //   return(
      //     <div key={i}>
      //       <p>{"Dados indisponíveis"}</p>
      //     </div>
      //   )
      // }
    }, this);


    let infoList = info.length > 0 && info.map((item, i) => {
      return(
        <option key={i} value={info[i].id}>{item.area.name}</option>
      )
    }, this);

    return(
      <div>
        <p>Select your region:</p>
        <select onChange={this.selectRegion}>
          { infoList }
        </select>
        <p>Current Season</p>
        <ul>
          { champs.name }{ ' - ' }{ champs.code }
          <li>{'Start date: '}{ champsCurrent.startDate }</li>
          <li>{'End date: '}{ champsCurrent.endDate }</li>
        </ul>
        <p>Previous Seasons</p>
        <ul>
          { seasonList }
        </ul> 
      </div>
    );
  }
}
