import React from 'react';
import './App.css';
import Todos from './components/Todos';
import axios from 'axios';

export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      info: '',
      champs: ''
    };
    this.selectRegion = this.selectRegion.bind(this);
  }
  // postLogin = async () => {
  //   const request = {
  //     method: 'POST',
  //     url: 'aaa',
  //     responseType: 'json'
  //   }
  // }
    //request.headers = {Authorization: token}
    // request.data = this.state
    // const response = await axios (request)
    // return response 
    // console.log('telho')

    selectRegion = async (e) => {
      //console.log(e)
      let idx = e.target.selectedIndex;
      const index = this.state.info[idx].id;
      console.log(index);
      await axios.get("http://api.football-data.org/v2/competitions/"+index.toString(),
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
          console.log(res.data.currentSeason);
          this.setState({champs: res.data.currentSeason});
          // console.log(this.state.champs);
        })
    }

    async componentWillMount() {
      await axios.get("http://api.football-data.org/v2/competitions/",
          { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
          'content-type' : 'application/json'}}
        ).then(res => {
          console.log(res.data.competitions);
          this.setState({info: res.data.competitions});


        })
    }

  //   getData = async () =>{
  //     const response =
  //       await axios.get("http://api.football-data.org/v2/areas/",
  //         { headers: {'X-Auth-Token': 'd5627ca1908f47b19b6e11d039d113da',
  //         'content-type' : 'application/json'}}
  //       )
  //     console.log(response.data)
  // }
  render() {
    const { info, champs } = this.state;
    console.log(champs.id);
    let infoList = info.length > 0 && info.map((item, i) => {
      //console.log(info[i].id);
      return(
        <option key={i} value={info[i].id}>{item.area.name}</option>
      )
    }, this);
    return(
      <div>
        <p>Select your region:</p>
        <select onChange={this.selectRegion}>
          { infoList }
        </select>
        <ul>
          <li>{ champs.name }{ ' - ' }{ champs.code }</li>
          <li>{'Data de início: '}{ champs.startDate}</li>
          <li>{'Data final: '}{ champs.endDate}</li>
        </ul>
      </div>
    );
        // <div>
        //   <ul>
        //     <li>{ this.state.info.areas }</li>
        //   </ul>
        // </div>

      // <div>
      // <h1 onClick={this.postLogin}>TESTE</h1>
      // <h1>{this.state.email}</h1> 
      // <Todos todos={this.state.todos} />
      // <form>
      // <input
      //   onChange={(e, v) => this.setState({email: v})} 
      //   value={this.state.email}
      //   placeholder='MANY'
      // />
      // </form>
      // </div>
  }
}
